INSERT INTO `wp_hnzihx_posts` VALUES("2331", "1", "2014-12-03 23:03:34", "2014-12-03 23:03:34", "Yes, please do! We can still fit you without the dress, but having the dress is the best way to ensure that your undergarments are a perfect fit with your dress.", "Can I bring my wedding dress to try on with undergarments?", "", "publish", "open", "closed", "", "can-i-bring-my-wedding-dress-to-try-on-with-undergarments", "", "", "2014-12-03 23:03:34", "2014-12-03 23:03:34", "", "0", "http://petticoatfair.com/?post_type=faq&amp;p=2331", "13", "faq", "", "0");
